CREATE TABLE `testDB`.`feedback_marker`(`id`            INT(11)     NOT NULL AUTO_INCREMENT,`assignment_id` INT(11)     NOT NULL,`user_id`       INT(11)     NOT NULL,`submission_id` INT(11)     NOT NULL,`priority`      INT(11)     NOT NULL,`status`        VARCHAR(50) NOT NULL,PRIMARY KEY (`id`)) ENGINE = InnoDB;
CREATE TABLE `testDB`.`feedback_error`(`id` INT(11) NOT NULL AUTO_INCREMENT,`assignment` INT(11) NOT NULL,`userid`       INT(11)  NOT NULL,`columnnumber` INT(11)  NOT NULL,`linenumber`   INT(11)  NOT NULL,`message` LONGTEXT NOT NULL,`position` INT(11)  NOT NULL, `filename` LONGTEXT NOT NULL,PRIMARY KEY (`id`)) ENGINE = InnoDB;
CREATE TABLE `testDB`.`feedback_testresult`(`id` INT(11) NOT NULL AUTO_INCREMENT,`assignment` INT(11) NOT NULL,`userid`     INT(11)     NOT NULL,`testname`   VARCHAR(50) NOT NULL,`result`LONGTEXT NOT NULL,PRIMARY KEY (`id`)) ENGINE = InnoDB;